import React from 'react';
import { View, Text, Image, TouchableOpacity } from 'react-native';

import { MaterialIcons } from '@expo/vector-icons';

import styles from './styles';

export default function Restaurant({ item }) {
	return (
		<View style={styles.container}>
			<Image
				style={styles.RestaurantImage}
				source={{
					uri: item.url
				}}
			/>
			<View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' }}>
				<Text style={styles.RestaurantTextName}>{item.nome}</Text>
				<TouchableOpacity style={styles.RestaurantBtn} onPress={() => addItemCart(item)}>
					<MaterialIcons name="add-shopping-cart" color="#fff" size={24} />
				</TouchableOpacity>
			</View>
		</View>
	);
}
